class Solution {public:    bool canJump(vector<int>& nums) {        int sz = nums.size();        int left = 0, right = 0;        while (left <= right) {            int rightMax = left;            for (int i = left; i <= right; ++i) {                if (nums[i] + i > rightMax) {                    rightMax = nums[i] + i >= sz ? sz - 1 : nums[i] + i;                }            }            left = right + 1;            right = rightMax;        }                return right == sz - 1;    }};